// node_modules/a/index.ts
import { a } from "a";
assert(a == 1);
import { a as a2 } from "a/";
assert(a2 == 1);
import { a as a3 } from "a/index";
assert(a3 == 1);

// node_modules/a/other.ts
import { a_other } from "a/other";
assert(a_other == 11);

// ../b/index.ts
import { b } from "b";
assert(b == 2);
import { b as b2 } from "b/";
assert(b2 == 2);
import { b as b3 } from "b/index";
assert(b3 == 2);

// ../b/other.ts
import { b_other } from "b/other";
assert(b_other == 22);

// ../b/other/index.ts
import { b_other_index } from "b/other/";
assert(b_other_index == 222);
import { b_other_index as b_other_index2 } from "b/other/index";
assert(b_other_index2 == 222);
